/* eslint-disable no-unused-vars */
import { useEffect, useState } from 'react'
import {
  faAngular,
  faCss3,
  faGitAlt,
  faHtml5,
  faJsSquare,
  faReact,
  faPython,
} from '@fortawesome/free-brands-svg-icons'
import Loader from 'react-loaders'
import AnimatedLetters from '../AnimatedLetters'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import './index.scss'

const About = () => {
  const aboutArray = 'ALBUM'.split('')

  const [letterClass, setLetterClass] = useState('text-animate')

  function App() {
    const [imagePath, setImagePath] = useState('/Users/song-wonjeong/Desktop/imagefolder/example.jpg'); // 원하는 이미지 경로로 변경
  
    // 이미지 경로가 변경되면 useEffect를 사용하여 이미지를 다시 로드
    useEffect(() => {
      // 이미지를 로드하여 표시할 때 fetch 또는 axios 등을 사용할 수 있습니다.
      // 아래는 fetch를 사용하는 예제입니다.
      fetch(imagePath)
        .then(response => {
          if (response.ok) {
            return response.blob();
          } else {
            throw new Error('이미지를 로드할 수 없습니다.');
          }
        })
        .then(blob => {
          const imageUrl = URL.createObjectURL(blob);
          // 이미지를 표시하기 위해 상태를 업데이트
          setImageUrl(imageUrl);
        })
        .catch(error => {
          console.error(error);
        });
    }, [imagePath]);
  
    return (
      <div>
        {/* 이미지를 표시하는 img 요소 */}
        {imageUrl && <img src={imageUrl} alt="이미지" />}
  
        {/* 이미지 경로 변경 버튼 */}
        <button onClick={() => setImagePath('/새로운/이미지/경로.jpg')}>
          다른 이미지 로드
        </button>
      </div>
    );
  }
  

  useEffect(() => {
    const timer = setTimeout(() => {
      setLetterClass('text-animate-hover')
    }, 2000)
    return () => clearTimeout(timer)
  }, [])

  return (
    <>
      <div className="container about-page">
        <div className="text-zone">
          <h1>
            <AnimatedLetters
              letterClass={letterClass}
              strArray={aboutArray}
              idx={15}
            />
          </h1>
          <p>
            여기는 앨범자리
          </p>
        </div>

        {/* <div className="stage-cube-cont">
          <div className="cubespinner">
            <div className="face1">
              <FontAwesomeIcon icon={faPython} color="#4B8BBE" />
            </div>
            <div className="face2">
              <FontAwesomeIcon icon={faHtml5} color="#F06529" />
            </div>
            <div className="face3">
              <FontAwesomeIcon icon={faCss3} color="#28A4D9" />
            </div>
            <div className="face4">
              <FontAwesomeIcon icon={faReact} color="#5ED4F4" />
            </div>
            <div className="face5">
              <FontAwesomeIcon icon={faJsSquare} color="#EFD81D" />
            </div>
            <div className="face6">
              <FontAwesomeIcon icon={faGitAlt} color="#EC4D28" />
            </div>
          </div>  사용언어 큐브 애니메이션
        </div> */} 
      </div>
      <Loader type="pacman" />
    </>
  )
}

export default About
