import React, { useEffect, useState, useRef } from 'react';
import WebcamComponent from './WebcamComponent'; // WebcamComponent를 가져옴
import AnimatedLetters from '../AnimatedLetters'; // AnimatedLetters를 가져옴
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import emailjs from '@emailjs/browser';
import './index.scss';

const cocoObjects = [
  "person", "bicycle", "car", "motorcycle", "airplane", "bus", "train", "truck", "boat", "traffic light",
  "fire hydrant", "stop sign", "parking meter", "bench", "bird", "cat", "dog", "horse", "sheep", "cow",
  "elephant", "bear", "zebra", "giraffe", "backpack", "umbrella", "handbag", "tie", "suitcase", "frisbee",
  "skis", "snowboard", "sports ball", "kite", "baseball bat", "baseball glove", "skateboard", "surfboard", 
  "tennis racket", "bottle", "wine glass", "cup", "fork", "knife", "spoon", "bowl", "banana", "apple",
  "sandwich", "orange", "broccoli", "carrot", "hot dog", "pizza", "donut", "cake", "chair", "couch",
  "potted plant", "bed", "dining table", "toilet", "tv", "laptop", "mouse", "remote", "keyboard", 
  "cell phone", "microwave", "oven", "toaster", "sink", "refrigerator", "book", "clock", "vase", 
  "scissors", "teddy bear", "hair drier", "toothbrush"
];

function CombinedComponent() {
  const [selectedObject, setSelectedObject] = useState('dog');
  const [objectCount, setObjectCount] = useState(1);
  const [shootingInterval, setShootingInterval] = useState(1);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [letterClass, setLetterClass] = useState('text-animate');
  const form = useRef();
  const contactArray = 'Select Objection'.split('');

  useEffect(() => {
    return setTimeout(() => {
      setLetterClass('text-animate-hover');
    }, 3000);
  }, []);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        process.env.REACT_APP_EMIAL_SERVICE_ID,
        process.env.REACT_APP_TEMPLATE_ID,
        form.current,
        process.env.REACT_APP_PUBLIC_KEY
      )
      .then(
        () => {
          toast.success('Message successfully sent!', {
            position: 'bottom-center',
            autoClose: 3500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: 'dark',
          });
          const timeout = setTimeout(() => {
            window.location.reload(false);
          }, 3900);

          return () => clearTimeout(timeout);
        },
        () => {
          toast.error('Failed to send the message, please try again', {
            position: 'bottom-center',
            autoClose: 3500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: 'dark',
          });
        }
      );
  };

  return (
    <div className="container contact-page">
      <div className="text-zone">
        <h1>
          <AnimatedLetters
            letterClass={letterClass}
            strArray={contactArray}
            idx={15}
          />
        </h1>
        <p>
          촬영할 객체를 선택하세요.
        </p>
        <div className="contact-form">
          <form ref={form} onSubmit={sendEmail}>
            <ul>
              <li>
                <select className="select_style">
                  <option value="test1">테스트1</option>
                  <option value="test2">테스트2</option>
                  <option value="test3">테스트3</option>
                  <option value="test4">테스트4</option>
                </select>
              </li>

              <li>
                <select className="select_style">
                  <option value="test1">테스트1</option>
                  <option value="test2">테스트2</option>
                  <option value="test3">테스트3</option>
                  <option value="test4">테스트4</option>
                </select>
              </li>
              <li>
                <input
                  placeholder="Num"
                  type="number"
                  name="Num"
                  required
                />
              </li>
              <li>
                <input type="submit" className="flat-button" value="SEND" />
              </li>
            </ul>
            <ToastContainer />
          </form>
        </div>
      </div>
      <Loader type="pacman" />

      <div className="Skills">
        <h1>Webcam Application</h1>

        <label htmlFor="objectSelect">Choose an object:</label>
        <select
          id="objectSelect"
          name="objectSelect"
          value={selectedObject}
          onChange={(e) => setSelectedObject(e.target.value)}
        >
          {cocoObjects.map(obj => <option key={obj} value={obj}>{obj.charAt(0).toUpperCase() + obj.slice(1)}</option>)}
        </select>

        <label htmlFor="objectCount">Number of objects:</label>
        <input
          type="number"
          id="objectCount"
          name="objectCount"
          min="1"
          max="10"
          value={objectCount}
          onChange={(e) => setObjectCount(e.target.value)}
        />

        <label htmlFor="shootingInterval">Shooting interval (seconds):</label>
        <input
          type="number"
          id="shootingInterval"
          name="shootingInterval"
          min="1"
          max="60"
          value={shootingInterval}
          onChange={(e) => setShootingInterval(e.target.value)}
        />

        <button onClick={() => setIsCameraActive(true)}>Complete Settings</button>

        {isCameraActive && (
          <WebcamComponent></WebcamComponent>
        )}
      </div>
    </div>
  );
}

export default CombinedComponent;
