import { useEffect, useState, useRef } from 'react'
import Loader from 'react-loaders'
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'
import emailjs from '@emailjs/browser'
import AnimatedLetters from '../AnimatedLetters'
import './index.scss'
import { ToastContainer, toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'
import React, { Component } from 'react';
import axios from 'axios';


const Contact = () => {
  const [letterClass, setLetterClass] = useState('text-animate')
  const form = useRef()
  const contactArray = 'Select Objection'.split('')


  // const currencies = [
  //   {
  //     value: 'USD',
  //     label: '$',
  //   },
  //   {
  //     value: 'EUR',
  //     label: '€',
  //   },
  //   {
  //     value: 'BTC',
  //     label: '฿',
  //   },
  //   {
  //     value: 'JPY',
  //     label: '¥',
  //   },
  // ]; 선택목록

  const [selectedCategory, setSelectedCategory] = useState(''); // 상위 카테고리 상태
  // const [subCategory, setSubCategory] = useState(''); // 하위 카테고리 상태
  const [categories, setCategories] = useState([]); // Coco 데이터셋에서 가져온 카테고리 목록

  // 야매코드용
  const [category, setCategory] = useState(''); // category 상태 정의
  const [subCategory, setSubCategory] = useState(''); // subCategory 상태 정의

  const subCategoriesByCategory = {
    Animal: ['개', '고양이', '코끼리', '곰', '얼룩말', '기린', '새', '말', '양', '소'],
    Transport: ['자전거', '차', '오토바이', '비행기', '버스', '기차', '트럭', '보트'],
  };

  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    setCategory(selectedCategory);

    // Category가 변경될 때 SubCategory 초기화
    setSubCategory('');
  };


  // useEffect(() => {
  //   return setTimeout(() => {
  //     setLetterClass('text-animate-hover')
  //   }, 3000)
  // }, [])

  useEffect(() => {
    // Coco 데이터셋 API 엔드포인트
    const apiUrl = 'https://api.coco-dataset.com/categories';

    axios.get(apiUrl)
      .then(response => {
        // API로부터 카테고리 목록을 가져오기
        const categories = response.data.categories;

        // 가져온 카테고리 목록을 상태에 설정
        setCategories(categories);
      })
      .catch(error => {
        console.error('카테고리 데이터를 가져오는 중 오류 발생:', error);
      });
  }, []);


  const sendEmail = (e) => {
    e.preventDefault()

    emailjs
      .sendForm(
        process.env.REACT_APP_EMIAL_SERVICE_ID,
        process.env.REACT_APP_TEMPLATE_ID,
        form.current,
        process.env.REACT_APP_PUBLIC_KEY
      )
      .then(
        () => {
          // alert('성공적으로 메세지를 보냈습니다.')
          toast.success('Message successfully sent!', {
            position: 'bottom-center',
            autoClose: 3500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: 'dark',
          })
          const timeout = setTimeout(() => {
            window.location.reload(false)
          }, 3900)

          return () => clearTimeout(timeout)
        },
        () => {
          // alert('다시 시도해주세요')
          toast.error('Failed to send the message, please try again', {
            position: 'bottom-center',
            autoClose: 3500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: 'dark',
          })
        }
      )
  }

  return (
    <>
      <div className="container contact-page">
        <div className="text-zone">
          <h1>
            <AnimatedLetters
              letterClass={letterClass}
              strArray={contactArray}
              idx={15}
            />
          </h1>
          <p>
            What object do you want to shoot?
          </p>
          <div className="contact-form">
            <form ref={form} onSubmit={sendEmail}>
              <ul>
                {/* <li>
                  <label>Category:</label>
                  <input
                    type="text"
                    name="Category"
                    placeholder="Category"
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    required
                  />
                </li>


                {selectedCategory && (
                  <>
                    <label>SubCategory:</label>
                    <input
                      type="text"
                      name="SubCategory"
                      placeholder="SubCategory"
                      value={subCategory}
                      onChange={(e) => setSubCategory(e.target.value)}
                      required
                    />
                  </>
                )} */}

                {/* 야매 코드. 스타일 적용 안됨 */}

                  <li>
                  <select
                    class="select_style"
                    name="Category"
                    value={category}
                    onChange={handleCategoryChange}
                    required
                  >
                    <option value="">Category</option>
                    <option value="Person">Person</option>
                    <option value="Animal">Animal</option>
                    <option value="Transport">Transport</option>
                  </select>
                  </li>
                  <li>
                  {category === 'Animal' && (
                    <>
                      <select
                      class="select_style"
                        name="SubCategory"
                        value={subCategory}
                        onChange={(e) => setSubCategory(e.target.value)}
                        required
                      >
                        <option value="">SubCategory</option>
                        {subCategoriesByCategory['Animal'].map((subCat) => (
                          <option key={subCat} value={subCat}>
                            {subCat}
                          </option>
                        ))}
                      </select>
                    </>
                  )}
                  </li>
                  <li>
                  {category === 'Transport' && (
                    <>
                      <label>SubCategory:</label>
                      <select
                      class="select_style"
                        name="SubCategory"
                        value={subCategory}
                        onChange={(e) => setSubCategory(e.target.value)}
                        required
                      >
                        <option value="">SubCategory</option>
                        {subCategoriesByCategory['Transport'].map((subCat) => (
                          <option key={subCat} value={subCat}>
                            {subCat}
                          </option>
                        ))}
                      </select>
                    </>
                  )}
                  </li>

                <li>
                  <input
                    placeholder="Num"
                    type="number"
                    name="Num"
                    required
                  />
                </li>

                <li>
                  <input type="submit" className="flat-button" value="SEND" />
                </li>
              </ul>
              <ToastContainer />
            </form>
          </div>
        </div>
      </div>
      <Loader type="pacman" />
    </>
  )
}

export default Contact
