import { useEffect, useState } from 'react';
import Loader from 'react-loaders';
import { useRef } from 'react';
import emailjs from '@emailjs/browser';
import AnimatedLetters from '../AnimatedLetters';
import './index.scss';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link, useNavigate } from 'react-router-dom';

const cocoObjects = [
  "person", "bicycle", "car", "motorcycle", "airplane", "bus", "train", "truck", "boat", "traffic light",
  "fire hydrant", "stop sign", "parking meter", "bench", "bird", "cat", "dog", "horse", "sheep", "cow",
  "elephant", "bear", "zebra", "giraffe", "backpack", "umbrella", "handbag", "tie", "suitcase", "frisbee",
  "skis", "snowboard", "sports ball", "kite", "baseball bat", "baseball glove", "skateboard", "surfboard", 
  "tennis racket", "bottle", "wine glass", "cup", "fork", "knife", "spoon", "bowl", "banana", "apple",
  "sandwich", "orange", "broccoli", "carrot", "hot dog", "pizza", "donut", "cake", "chair", "couch",
  "potted plant", "bed", "dining table", "toilet", "tv", "laptop", "mouse", "remote", "keyboard", 
  "cell phone", "microwave", "oven", "toaster", "sink", "refrigerator", "book", "clock", "vase", 
  "scissors", "teddy bear", "hair drier", "toothbrush"
];

const Contact = () => {
  const [letterClass, setLetterClass] = useState('text-animate');
  const form = useRef();
  const contactArray = 'Select Objection'.split('');

  const [selectedObject, setSelectedObject] = useState('dog');
  const [objectCount, setObjectCount] = useState(1);
  const [shootingInterval, setShootingInterval] = useState(1);
  const [detectionMode, setDetectionMode] = useState('above');
  const navigate = useNavigate();

  useEffect(() => {
    setTimeout(() => {
      setLetterClass('text-animate-hover');
    }, 3000);
  }, []);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        process.env.REACT_APP_EMAIL_SERVICE_ID,
        process.env.REACT_APP_TEMPLATE_ID,
        form.current,
        process.env.REACT_APP_PUBLIC_KEY
      )
      .then(
        () => {
          toast.success('Message successfully sent!', {
            position: 'bottom-center',
            autoClose: 3500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: 'dark',
          });
          const timeout = setTimeout(() => {
            window.location.reload(false);
          }, 3900);
          return () => clearTimeout(timeout);
        },
        () => {
          toast.error('Failed to send the message, please try again', {
            position: 'bottom-center',
            autoClose: 3500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: 'dark',
          });
        }
      );
  };

  const handleSettingComplete=()=> {
    let settings={
      selectedObject,
      objectCount:parseInt(objectCount),
      shootingInterval:parseInt(shootingInterval),
      detectionMode,
    };

    navigate('../skills', { state: { settings } });
 };

  return (
    <>
      <div className="container contact-page">
        <div className="text-zone">
          <h1>
            <AnimatedLetters letterClass={letterClass} strArray={contactArray} idx={15} />
          </h1>
          <div>
            <label htmlFor="objectSelect">촬영할 객체를 선택하세요!</label>
            <select
              id="objectSelect"
              name="objectSelect"
              value={selectedObject}
              onChange={(e) => setSelectedObject(e.target.value)}
            >
              {cocoObjects.map((obj) => (
                <option key={obj} value={obj}>
                  {obj.charAt(0).toUpperCase() + obj.slice(1)}
                </option>
              ))}
            </select>
            <label htmlFor="shootingInterval">촬영간격(초)을 선택하세요!</label>
            <input
              type="number"
              id="shootingInterval"
              name="shootingInterval"
              min="0.1"
              max="60"
              value={shootingInterval}
              onChange={(e) => setShootingInterval(e.target.value)}
            />
            <label htmlFor="objectCount">객체 수를 선택하세요!</label>
            <input
              type="number"
              id="objectCount"
              name="objectCount"
              min="1"
              max="10"
              value={objectCount}
              onChange={(e) => setObjectCount(e.target.value)}
            />
            <div>
              <label>
                <input
                  type="radio"
                  value="above"
                  checked={detectionMode === 'above'}
                  onChange={(e) => setDetectionMode(e.target.value)}
                />
                이상
              </label>
              <label>
                <input
                  type="radio"
                  value="below"
                  checked={detectionMode === 'below'}
                  onChange={(e) => setDetectionMode(e.target.value)}
                />
                이하
              </label>
              <label>
                <input
                  type="radio"
                  value="fix"
                  checked={detectionMode === 'fix'}
                  onChange={(e) => setDetectionMode(e.target.value)}
                />
                고정
              </label>
            </div>
            <div style={{ width: '80%', textAlign: 'center' }}>
              <button onClick={handleSettingComplete}>세팅 완료!</button>
            </div>
          </div>
        </div>
      </div>
      <Loader type="pacman" />
    </>
  );
};

export default Contact;
